<?phpclass Database{		public static function getConnection()		{				try {						$envPath = __DIR__ . '/../env.ini';						$env = parse_ini_file($envPath);						$conn = new PDO('mysql:host=' . $env['host'] . ';dbname=' . $env['database'],								$env['username'],								$env['password'],								$env						);				} catch (PDOException $exception) {						die("Erro: " . $exception->getMessage());				}				return $conn;		}		public static function getResultFromQuery($sql)		{				$conn = self::getConnection();				$result = $conn->query($sql);				$conn->errorInfo();				return $result;		}		final private function __construct()		{		}		final private function __clone()		{		}}